var text={tohtml:function(){},tojson:function(txt){return null==txt?null:"["==txt.trim().indexOf[0]?eval(txt.replace(/\r/g,"\\r").replace(/\n/g,"\\n")):eval("("+txt.replace(/\r/g,"\\r").replace(/\n/g,"\\n")+")")}};